mapper.update:
UPDATE INVENTORY SET
QTY = QTY - #{increment}
WHERE ITEMID = #{itemId}

mapper.select:
SELECT QTY AS value
FROM INVENTORY
WHERE ITEMID = #{itemId}

mapper.update:
update inventory
set itemid = #{id}
where itemid = #{initId}

update item
set status = #{status}
where itemid = #{itemId}

mapper.delete:
delete a, b
from item a left join inventory b on a.itemid = b.itemid
where a.itemid = #{itemId}
